import { FavoriteBorderOutlined, SearchOutlined, ShoppingCartOutlined } from '@material-ui/icons';
import React from 'react'
import styled from 'styled-components';

const Container = styled.div`
flex : 1 ;
margin:5px;
min-width:280px;
min-height:350px;
display:flex;
align-items:center;
justify-content:center;
background-color:#f5fbfd;
position:relative`

const Circle = styled.div`
width:200px;
height:200px;
border-radius:50%;
background-color:white;
position:absolute;`
const Image = styled.img`
width:75%;
height:75%;
z-index:2;`
const Icon = styled.div`
width:40px;
height:40px;
border-radius:50%;
background-color:white;
display:flex;
align-items:center;
justify-content:center;
margin:10px;

`
const Info = styled.div`
position:absolute;
top:0;
left:0;
width:100%;
height:100%;
display:flex;
background-color : gray;
align-items:center;
justify-content:center;
opicity:0.5;
z-index:3;`
const Product=({item})=> {
    return (
<Container>
    <Circle />
    <Image src={item.img}/>
    <Info>
    <Icon>
        <ShoppingCartOutlined/>
    </Icon>
    <Icon>
        <SearchOutlined/>
    </Icon>
    <Icon>
        <FavoriteBorderOutlined/>
    </Icon></Info>
</Container>
        
    )
}
export default Product;
