import { Add, Remove } from '@material-ui/icons';
import React from 'react'
import styled from 'styled-components';


const Container = styled.div``
const Wrapper = styled.div`
padding:50px;
display:flex;`
const ImageContainer = styled.div`
flex:1;
`
const Image = styled.img`
width:100%;
height:90vh;
object-fit:cover;`
const InfoContainer = styled.div
`
flex:1;
padding: 0px 50px;`
const Title = styled.h3`
font-weight:300;`
const Desc = styled.p`
margin:20px 0px;`
const Price = styled.span
`
font-weight:100;
font-size:40px;`
const AddContainer= styled.div`
display:flex;
width:50%;
justify-content:space-between;
align-items:center;`
const AmountContainer = styled.div`
display:flex;
align-items:center;
font-weight:700;`

const Amount = styled.span`
width:30px;
height:30px;
border-radius:10px;
border:1px solid gray; 
display:flex;
 align-items:center;
justify-content:center; 
margin:0px 5px;`

const Button = styled.button
`
margin :5px;
padding:5pxpx;
border:2px solid gray;
background-color:white;
cursor:pointer;
font-weightL500;
&:hover{
 background-color : #f8f4f4;  

}`

const Product=()=> {
    return (
     <Container>
         <Wrapper>
<ImageContainer>
    <Image src= {require('../image/img4.jpeg').default}></Image>
</ImageContainer>
<InfoContainer>
    <Title>Table Lamp</Title>
    <Desc>Bwest and high quality wood</Desc>
    <Price>50$</Price>
    <AddContainer>
        <AmountContainer>
            <Remove/>
            
            <Amount>1</Amount>
<Add/>
        </AmountContainer>
        <Button>ADD TO CART</Button>
    </AddContainer>
</InfoContainer>
         </Wrapper>
     </Container>
    )
}
export default Product ;
