import { createSlice } from "@reduxjs/toolkit";


const userSlice = createSlice({
    name:"user",
    initialState:{
        currentUser:null,
        isFetching:false,
        erorr:false,
    },
    reducers:{
        loginStart:(state )=>{state.isFetching = true },
        loginSuccess:(state,action)=>{
            state.isFetching=false;
            state.currentUser=action.payload
        },
        loginFailuer:(state)=>{
            state.isFetching= false;
            state.erorr = true;
        }
    },
    logoutStart: (state) => {
        state.isFetching = true;
        state.error = false;
      },
      logoutSuccess: (state, action) => {
        state.isFetching = false;
        state.currentUser.splice(
          state.currentUser.findIndex((user) => user._id === action.payload),
          1
        );
      },
      logoutFailure: (state) => {
        state.isFetching = false;
        state.error = true;
      }
});
export const {loginStart ,loginSuccess ,loginFailuer, logoutStart, logoutSuccess,logoutFailure} = userSlice.actions;
export default userSlice.reducer;
export const usersSelector = (state )=> state.user;
export const userSelector = (state )=> state.user.currentUser;
