import React ,{useState } from 'react'
      //import { Button } from '@material-ui/core';
        import styled from 'styled-components';
import { login } from '../redux/apiCalles';
      import { mobile } from '../responsive';

      import {useDispatch ,useSelector} from 'react-redux';
      const Container =styled.div`
      width:100vw;
      height:100vh;
      background-color:#EEE2DC;
      background:linear-gradient(rgba(225,225,225,0.5),rgba(225,225,225,0.5));
      display:flex;
      align-items:center;
      justify-content:center;
      `
      const Wrapper =styled.div`
      padding:20px;
      width:40%;
      background-color:white;
      border:2px solid gray;
      ${mobile({width:"75%" , alignItems:"center"})}; `
      const Form =styled.form`
      display:flex;
      flex-wrap:wrap;
      flex-direction:column;`
      const Input =styled.input`
      flex:1;
      min-width:40%;
      margin:20px 10px 0px 5px;
      padding:10px;`
      const Button =styled.button`
      
      display:flex;
      justify-content:center;
      align-items:center;
      width:40%;
      font-size:15px;
      margin-top :20px;
      padding:15px;
      margin-left:100px;
      border:2px solid gray;
      background-color:gray;
      cursor:pointer;
      font-weight:500;
      
      &:hover{
       background-color : #f8f4f4;
      };
      &disabled{
        color:white;
        cursor:not-allowed;
      };
      ${mobile({marginLeft:"70px"})};`
      const Title =styled.h1
      `
      font-size:24px;
      font-weight:300px;`
      const Link = styled.a`
margin:10px 0px;
font-size:12px;
text-decoration:underline;
cursor:pointer;`
const Error = styled.span`
color:red;`
      
const Login =()=> {
  const [email ,setemail]=useState("");
  const [password ,setpassword]=useState();
  const [error,seterror]= useState(false);
  const  dispatch = useDispatch();
  const {isFitching }=useSelector((state) => state.user);
  const handlelogin=(e)=>{
  try{//to prevent refresh login button
  seterror(false);
e.preventDefault();
login(dispatch, {email,password});
 } catch(err){
   seterror(true);
 }
  };
    return (


       <Container>
           <Wrapper>
           <Title>SIGN IN </Title>
           <Form>
             
               <Input placeholder= "E-Mail" onChange={(e) =>setemail(e.target.value)}/>
               <Input placeholder= "Password" type="password"  onChange={(e) =>setpassword(e.target.value)} />
             
               <Button onClick = {handlelogin} disabled={isFitching}>Log IN</Button>
             { error && <Error>Somthing Can Be Wrong</Error>}
               <Link>Register Here</Link>
         </Form>
       </Wrapper>
       </Container>


    )
}
export default  Login;
