//import { Button } from '@material-ui/core';
import React,{useState} from 'react'
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { userRequest } from '../requestMethod';
import { mobile } from '../responsive';
import AuthService from "../services/auth.services";

const Container =styled.div`
width:100vw;
height:100vh;
background-color:#EEE2DC;
background:linear-gradient(rgba(225,225,225,0.5),rgba(225,225,225,0.5));
display:flex;
align-items:center;
justify-content:center;
`
const Wrapper =styled.div`
padding:20px;
width:40%;
background-color:white;
border:2px solid gray;
${mobile({width:"75%"})};`
const Form =styled.form`
display:flex;
flex-wrap:wrap;
flex-direction:column;`
const Input =styled.input`
flex:1;
min-width:40%;
margin:20px 10px 0px 5px;
padding:10px;`
const Button =styled.button`

display:flex;
justify-content:center;
align-items:center;
width:40%;
font-size:15px;
margin-top :20px;
padding:15px;
margin-left:100px;
border:2px solid gray;
background-color:gray;
cursor:pointer;
font-weight:500;

&:hover{
 background-color : #f8f4f4;
};
${mobile({marginLeft:"70px"})};`
const Title =styled.h1
`
font-size:24px;
font-weight:300px;`
const Error = styled.span`
color:red;
font-size:14px;
font-weight:500;`


const Register =()=> {
const [username,setusername]= useState("");
const[email,setemail]=useState("");
const[password,setpassword]=useState("");
const[error,seterror]=useState(false);
const [message, setMessage] = useState("");
const handleSubmit =async(e)=>{
    e.preventDefault();
    // seterror(false);
//     try{
//     const res = await userRequest.post("/auth/register",{
//         username,
//         email,password
//     });
// res.data && window.location.replace("/");}catch(err){
//         seterror(true);
//     }
AuthService.register(username, email, password).then(
    (response) => {
      setMessage(response.data.message);
     
    },
    (error) => {
      const resMessage =
        (error.response &&
          error.response.data &&
          error.response.data.message) ||
        error.message ||
        error.toString();
        setMessage(resMessage);
       
      }
    );
  }

    return (
       <Container>
           <Wrapper>
           <Title>Register </Title>
           <Form onSubmit={handleSubmit}>
               <Input placeholder= "userName" onChange={(e)=>setusername(e.target.value)} />
             
               <Input placeholder= "E-Mail" onChange={(e)=>setemail(e.target.value)}/>
               <Input placeholder= "Password" onChange={(e)=>setpassword(e.target.value)}/>
         
               <Button type="submit">Register</Button>
               {error && <Error>Somthing Can Be Wrong</Error>}
         </Form>
 
    
    </Wrapper>
       </Container>
    )
}
export default  Register;
