import React ,{useState } from 'react'
      //import { Button } from '@material-ui/core';
        import styled from 'styled-components';
import { login } from '../redux/apiCalles';
      import { mobile } from '../responsive';

      import {useDispatch ,useSelector} from 'react-redux';
import {  usersSelector } from '../redux/userRedux';
import { useHistory } from 'react-router';

      const Container =styled.div`
      width:100vw;
      height:100vh;
      background-color:#EEE2DC;
      background:linear-gradient(rgba(225,225,225,0.5),rgba(225,225,225,0.5));
      display:flex;
      align-items:center;
      justify-content:center;
      `
      const Wrapper =styled.div`
      padding:20px;
      width:40%;
      background-color:white;
      border:2px solid gray;
      ${mobile({width:"75%" , alignItems:"center"})}; `
      const Form =styled.form`
      display:flex;
      flex-wrap:wrap;
      flex-direction:column;`
      const Input =styled.input`
      flex:1;
      min-width:40%;
      margin:20px 10px 0px 5px;
      padding:10px;`
      const Button =styled.button`
      
      display:flex;
      justify-content:center;
      align-items:center;
      width:40%;
      font-size:15px;
      margin-top :20px;
      padding:15px;
      margin-left:100px;
      border:2px solid gray;
      background-color:gray;
      cursor:pointer;
      font-weight:500;
      
      &:hover{
       background-color : #f8f4f4;
      };
      &disabled{
        color:white;
        cursor:not-allowed;
      };
      ${mobile({marginLeft:"70px"})};`
      const Title =styled.h1
      `
      font-size:24px;
      font-weight:300px;`
      const Link = styled.a`
margin:10px 0px;
font-size:12px;
text-decoration:underline;
cursor:pointer;`
const Error = styled.span`
color:red;`
      
const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const dispatch = useDispatch();
  const { isFetching, error } = useSelector(usersSelector);

  const handleClick = (e) => {
    e.preventDefault();
   dispatch(login({ username, password,loggedin:true }));
  };
  return (
    <Container>
      <Wrapper>
        <Title>SIGN IN</Title>
        <Form>
          <Input
            placeholder="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <Input
            placeholder="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <Button onClick={handleClick} disabled={isFetching}>
            LOGIN
          </Button>
          {error && <Error>Something went wrong...</Error>}
          <Link>DO NOT YOU REMEMBER THE PASSWORD?</Link>
          <Link>CREATE A NEW ACCOUNT</Link>
        </Form>
      </Wrapper>
    </Container>
  );
};

export default Login;
