import React , {useState} from 'react'
import styled from 'styled-components'
import { mobile } from '../responsive'
import { useLocation } from 'react-router'
import Products from '../shared/components/Products'
const Container = styled.div`
`
const FilterContainer = 
styled.div`
display:flex;
justify-content:space-between`
const Title = styled.h2`
margin:20px;
`
const Filter = styled.div`
margin:20px;
${mobile({width:"0px 20px" , display:"flex" , flexDirection:"column"})};`

const FilterText = styled.span`
font-size:20px;
font-weight:600;
${mobile({marginRight:"0px" , fontSize:"15px"})};
`
const Select = styled.select`
padding:10px;
margin:20px;
${mobile({margin:"10px 0px"})};`
const Option = styled.option``
const ProductList=()=> {
    const location = useLocation();
    const  cat =location.pathname.split("/")[2];
  const [filter, setFilter] = useState({});
  const handelFilters = (e)=>{
      const value = e.target.value;
      setFilter({
          ...filter,
          [e.target.name]:value ,
      });
  };
  console.log(filter)
    return (
    <Container>
        <Title>Wood Product Shop</Title>
        <FilterContainer>
            
            <Filter><FilterText>Product Price</FilterText>
            <Select name="price" onChange={handelFilters}><Option disabled >Price</Option>
            <Option>less than 100Dhs</Option>
            <Option>more than 100Dhs</Option>
           </Select></Filter>
            <Filter><FilterText>Product Size</FilterText>
            <Select name="size" onChange={handelFilters}><Option disabled >Size</Option>
            <Option>Small</Option>
            <Option>Meduim</Option>
            <Option>Large</Option>
           </Select>
           </Filter>
        </FilterContainer>
        <Products />
    </Container>
    )
}

export default ProductList;
