import { ArrowLeftOutlined,  ArrowRightOutlined } from "@material-ui/icons";
import React from "react";
import styled from "styled-components";
import myImage from '../../image/fkcBiDWQ.jpeg'

const Container = styled.div`
width :100%;
height:100vh;
display:flex;
background-color:white;
position:relative;
`
const Arrow =styled.div`
width:50px;
height:50px;
background-color:white ;
border-radius:50% ;
display : flex ;
align-items : center ;
justify-content : center ;
position : absolute ;
top:0;
bottom:0;
left:${props=>props.direction === "left"&&"10px"};
right:${props=>props.direction === "right"&&"10px"};
margin:auto;
cursor:pointer;
opacity:0.5 ;`
const Wrapper = styled.div`
height=100px;
display:flex;
`
const Slide = styled.div`
width:100vw;
height : 100vh;
display:flex;
align-items:center;
background-color : #${props =>props.bg};
`
const ImageContanier= styled.div`
margin-top: 20px;
align-items:center;
display:flex;
flex : 1 ;`
const Image = styled.img`
height:90%


`
const InfoContanier = styled.div`
margin-top:70px;
align-items:center;
flex:1;
`
const Title = styled.h1`
font-size:70px;
`
const Par = styled.p`
margin : 50px 0px;
font-size: 20px;
font-weight:500;
letter-spacing : 3px;
`
const Button = styled.button`
padding:10px;
font-size:20px;
background-color:transparent;
align-items:center;

cursor:pointer;

`

const Slider =()=>{
return(
    <Container>
      <Arrow direction="left">
        <ArrowLeftOutlined />
    </Arrow>
    <Wrapper>
<Slide bg="f5fafd">
      <ImageContanier><Image  src={myImage}/></ImageContanier>
      <InfoContanier><Title>Natural Wood Product</Title>
      <Par>Amazing wood Made From Nature </Par>
      <Button>Show All Product</Button></InfoContanier>
      </Slide>
      <Slide bg="f5fafd">
      <ImageContanier><Image  src={myImage}/></ImageContanier>
      <InfoContanier><Title>Natural Wood Product</Title>
      <Par>Amazing wood Made From Nature </Par>
      <Button>Show All Product</Button></InfoContanier>
      </Slide>
      <Slide bg="f5fafd">
      <ImageContanier><Image  src={myImage}/></ImageContanier>
      <InfoContanier><Title>Natural Wood Product</Title>
      <Par>Amazing wood Made From Nature </Par>
      <Button>Show All Product</Button></InfoContanier>
      </Slide>
    </Wrapper>
    <Arrow direction="right"> 
      <ArrowRightOutlined/>
    </Arrow>
    </Container>)


};

export default Slider;
